<?php

namespace common\widgets;

use Yii;
use yii\helpers\Html;
use yii\web\JsExpression;

class StudioGood extends \yii\bootstrap\Widget
{
    public $logo = 'data:image/png;base64,';
    public $linkText = 'Разработка и поддержка сайта';
    public $outerText = ' — студия Хорошая идея';
    public $url = 'http://studio-good.ru';

    public function run()
    {
        $image = Html::img($this->logo, ['style' => 'height:30px;width:auto;margin-right:10px;']);
        return Html::a($image . $this->linkText, $this->url, ['target' => '_blank','rel'=>'noopener']) . $this->outerText;
    }
}
